echo "---"

echo "WallAware | size=24 color=blue"
networksetup -getairportnetwork en0 | sed -E 's,^Current Wi-Fi Network: (.+)$,\1,'
echo "---"

echo "Refresh SSID | refresh=true"
echo "Change Wallpaper Now | bash=~/WallAware/WallAware.sh" terminal=false

echo "---"
